import java.util.*;

class CourseScheduler {
    static class Result {
        boolean canFinish;
        int[] order;

        Result(boolean canFinish, int[] order) {
            this.canFinish = canFinish;
            this.order = order;
        }
    }

    public Result scheduleCourses(int numCourses, int[][] prerequisites) {
        // Build adjacency list representation of the graph
        List<List<Integer>> graph = new ArrayList<>();
        for (int i = 0; i < numCourses; i++) {
            graph.add(new ArrayList<>());
        }

        // Calculate in-degrees for each course
        int[] inDegree = new int[numCourses];

        // Process prerequisites to build graph and in-degrees
        for (int[] prereq : prerequisites) {
            int course = prereq[0];
            int prerequisite = prereq[1];

            // Add edge from prerequisite to course
            graph.get(prerequisite).add(course);

            // Increment in-degree of course
            inDegree[course]++;
        }

        // Find courses with no prerequisites (in-degree = 0)
        Queue<Integer> queue = new LinkedList<>();
        for (int i = 0; i < numCourses; i++) {
            if (inDegree[i] == 0) {
                queue.offer(i);
            }
        }

        // Process courses in topological order
        int[] order = new int[numCourses];
        int index = 0;
        int processedCourses = 0;

        while (!queue.isEmpty()) {
            int currentCourse = queue.poll();
            order[index++] = currentCourse;
            processedCourses++;

            // Reduce in-degree of dependent courses
            for (int dependentCourse : graph.get(currentCourse)) {
                inDegree[dependentCourse]--;
                // If in-degree becomes zero, add to queue
                if (inDegree[dependentCourse] == 0) {
                    queue.offer(dependentCourse);
                }
            }
        }

        // Check for cycles
        boolean canFinish = (processedCourses == numCourses);

        // If there's a cycle, return empty order
        if (!canFinish) {
            return new Result(false, new int[0]);
        }

        return new Result(true, order);
    }
}

