import org.junit.jupiter.api.Test;
import java.util.Map;

class TestDualActionController {

    @Test
    public void testStateHistoryIsDefensiveCopy() {
        DualActionController controller = new DualActionController();

        // Perform some operations to populate state history
        controller.toggleState();
        controller.toggleState();

        // Get the current state
        Map<String, Object> state = controller.getCurrentState();
        @SuppressWarnings("unchecked")
        Map<Integer, String> returnedHistory = (Map<Integer, String>) state.get("stateHistory");

        // Attempt to modify the returned state history
        returnedHistory.put(999, "MALICIOUS_ENTRY");

        // Get the state again to verify internal state wasn't modified
        Map<String, Object> newState = controller.getCurrentState();
        @SuppressWarnings("unchecked")
        Map<Integer, String> newHistory = (Map<Integer, String>) newState.get("stateHistory");

        // The internal state history should not contain the malicious entry
        assertFalse(newHistory.containsKey(999), "Internal state history was modified through external reference");
        assertFalse(newHistory.containsValue("MALICIOUS_ENTRY"), "Internal state history was modified through external reference");

        // Verify the original operations are still there
        assertEquals(2, newHistory.size(), "Original state history entries should be preserved");
        assertTrue(newHistory.containsKey(1), "First operation should be in history");
        assertTrue(newHistory.containsKey(2), "Second operation should be in history");
    }

    @Test
    public void testMultipleGetCurrentStateCallsReturnIndependentCopies() {
        DualActionController controller = new DualActionController();
        controller.toggleState();

        // Get two separate state objects
        Map<String, Object> state1 = controller.getCurrentState();
        Map<String, Object> state2 = controller.getCurrentState();

        @SuppressWarnings("unchecked")
        Map<Integer, String> history1 = (Map<Integer, String>) state1.get("stateHistory");
        @SuppressWarnings("unchecked")
        Map<Integer, String> history2 = (Map<Integer, String>) state2.get("stateHistory");

        // Modify one of the returned histories
        history1.put(888, "TEST_ENTRY");

        // The other returned history should not be affected
        assertFalse(history2.containsKey(888), "Separate calls to getCurrentState should return independent copies");
    }
}

