class DataProcessor {
    public Map<String, Object> processData(List<Map<String, String>> items, Map<String, Object> config) {
        List<Map<String, String>> processedItems = new ArrayList<>();
        int transformedCount = 0;
        int emptyFieldCount = 0;
        int specialCaseCount = 0;

        Boolean mergeFields = (Boolean) config.get("mergeFields");
        String mergeSeparator = (String) config.get("mergeSeparator");
        Boolean countEmpty = (Boolean) config.get("countEmpty");
        Integer maxLength = (Integer) config.get("maxLength");
        if (mergeSeparator == null) {
            mergeSeparator = " - ";
        }

        for (Map<String, String> item : items) {
            Map<String, String> processedItem = new HashMap<>(item);

            // Check for special cases
            String code = item.get("code");
            if (code != null && code.startsWith("SPECIAL")) {
                specialCaseCount++;
            }

            if (mergeFields != null && mergeFields && 
                item.containsKey("code") && item.containsKey("name")) {
                String name = item.get("name");
                String merged = code + mergeSeparator + name;

                if (maxLength != null && merged.length() > maxLength) {
                    merged = merged.substring(0, maxLength) + "...";
                }

                processedItem.put("merged", merged);
                transformedCount++;
            }

            if (countEmpty != null && countEmpty) {
                for (String value : item.values()) {
                    if (value == null || value.isEmpty()) {
                        emptyFieldCount++;
                        break;
                    }
                }
            }

            processedItems.add(processedItem);
        }

        Map<String, Object> result = new HashMap<>();
        result.put("processedItems", processedItems);
        result.put("totalItems", items.size());
        result.put("emptyFieldCount", emptyFieldCount);
        result.put("transformedCount", transformedCount);
        result.put("specialCaseCount", specialCaseCount);

        return result;
    }
}

