    public void testManagerExtendsEmployee() {
        Manager manager = new Manager("John Doe", 75000.0);
        assertTrue(manager instanceof Employee);
        assertEquals("John Doe", manager.getName());
        assertEquals(75000.0, manager.getSalary());
    }

    @Test
    public void testManagerConstructorInitializesSubordinates() {
        Manager manager = new Manager("Alice Smith", 80000.0);
        assertNotNull(manager.getSubordinates());
        assertEquals(0, manager.getTeamSize());
    }

    @Test
    public void testAddSubordinate() {
        Manager manager = new Manager("Bob Johnson", 85000.0);
        Employee emp1 = new Employee("Jane Doe", 50000.0);
        Employee emp2 = new Employee("Mike Wilson", 55000.0);

        manager.addSubordinate(emp1);
        assertEquals(1, manager.getTeamSize());

        manager.addSubordinate(emp2);
        assertEquals(2, manager.getTeamSize());
    }

    @Test
    public void testGetSubordinatesReturnsCopy() {
        Manager manager = new Manager("Carol Brown", 90000.0);
        Employee emp = new Employee("David Lee", 45000.0);

        manager.addSubordinate(emp);
        List<Employee> subordinates1 = manager.getSubordinates();
        List<Employee> subordinates2 = manager.getSubordinates();

        // Should return different list instances (copies)
        assertNotSame(subordinates1, subordinates2);

        // But with same content
        assertEquals(subordinates1.size(), subordinates2.size());
        assertEquals(subordinates1.get(0), subordinates2.get(0));

        // Modifying returned list should not affect manager's internal list
        subordinates1.clear();
        assertEquals(1, manager.getTeamSize());
    }

    @Test
    public void testGetTeamSize() {
        Manager manager = new Manager("Eve Davis", 95000.0);
        assertEquals(0, manager.getTeamSize());

        manager.addSubordinate(new Employee("Frank Miller", 48000.0));
        assertEquals(1, manager.getTeamSize());

        manager.addSubordinate(new Employee("Grace Taylor", 52000.0));
        manager.addSubordinate(new Employee("Henry Clark", 49000.0));
        assertEquals(3, manager.getTeamSize());
    }
}

