import java.util.*;
import java.util.function.Function;

class MessageProcessor {
    private final Set<String> processedMessageIds;
    private final Map<String, Function<String, String>> handlers;
    private final List<RoutingRule> routingRules;
    private final Queue<String> messageQueue;

    private static class RoutingRule {
        final String pattern;
        final String handlerKey;

        RoutingRule(String pattern, String handlerKey) {
            this.pattern = pattern;
            this.handlerKey = handlerKey;
        }
    }

    public MessageProcessor() {
        // Initialize all data structures
        this.processedMessageIds = new HashSet<>();
        this.handlers = new HashMap<>();
        this.routingRules = new ArrayList<>();
        this.messageQueue = new LinkedList<>();
    }

    public void addHandler(String messageType, Function<String, String> handler) {
        handlers.put(messageType, handler);
    }

    public String processMessage(String messageId, String messageType, String content) {
        // Process a single message with deduplication
        if (processedMessageIds.contains(messageId)) {
            return "Message already processed";
        }

        processedMessageIds.add(messageId);

        // Apply handler if exists for the messageType
        Function<String, String> handler = handlers.get(messageType);
        if (handler == null) {
            return "No handler found for message type: " + messageType;
        }

        return handler.apply(content);
    }

    public void addRoutingRule(String pattern, String handlerKey) {
        routingRules.add(new RoutingRule(pattern, handlerKey));
    }

    public String findHandlerKey(String path) {
        for (RoutingRule rule : routingRules) {
            if (matchesPattern(rule.pattern, path)) {
                return rule.handlerKey;
            }
        }
        return null;
    }

    private boolean matchesPattern(String pattern, String path) {
        String[] patternSegments = pattern.split("/");
        String[] pathSegments = path.split("/");

        return matchSegments(patternSegments, 0, pathSegments, 0);
    }

    private boolean matchSegments(String[] patternSegments, int patternIndex, 
                                 String[] pathSegments, int pathIndex) {
        // Base cases
        if (patternIndex >= patternSegments.length && pathIndex >= pathSegments.length) {
            return true;
        }
        if (patternIndex >= patternSegments.length) {
            return false;
        }

        String currentPattern = patternSegments[patternIndex];

        if ("#".equals(currentPattern)) {
            // # can match one or more segments
            // Try matching from current path position onwards
            for (int i = pathIndex; i < pathSegments.length; i++) {
                if (matchSegments(patternSegments, patternIndex + 1, pathSegments, i + 1)) {
                    return true;
                }
            }
            // Also try if # is at the end and can match remaining path
            return patternIndex == patternSegments.length - 1;
        } else if ("*".equals(currentPattern)) {
            // * can match exactly one segment that doesn't contain dots
            if (pathIndex >= pathSegments.length) {
                return false;
            }
            String currentPathSegment = pathSegments[pathIndex];
            if (currentPathSegment.contains(".")) {
                return false;
            }
            return matchSegments(patternSegments, patternIndex + 1, pathSegments, pathIndex + 1);
        } else {
            // Exact match required
            if (pathIndex >= pathSegments.length || !currentPattern.equals(pathSegments[pathIndex])) {
                return false;
            }
            return matchSegments(patternSegments, patternIndex + 1, pathSegments, pathIndex + 1);
        }
    }

    public void enqueueMessage(String messageId, String messageType, String content) {
        String formattedMessage = messageId + "|" + messageType + "|" + content;
        messageQueue.offer(formattedMessage);
    }

    public Map<String, String> processQueue() {
        Map<String, String> results = new HashMap<>();

        while (!messageQueue.isEmpty()) {
            String message = messageQueue.poll();
            String[] parts = message.split("\\|", 3);

            if (parts.length == 3) {
                String messageId = parts[0];
                String messageType = parts[1];
                String content = parts[2];

                String result = processMessage(messageId, messageType, content);
                results.put(messageId, result);
            }
        }

        return results;
    }
}

