import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.*;
import java.util.function.Function;

class TestMessageProcessor {

    @Test
    public void testEnqueueMessage() {
        MessageProcessor processor = new MessageProcessor();
        processor.addHandler("INFO", content -> "Processed: " + content);

        // Enqueue a message
        processor.enqueueMessage("msg1", "INFO", "test content");

        // Process the queue and verify the message was processed
        Map<String, String> results = processor.processQueue();
        assertEquals(1, results.size());
        assertEquals("Processed: test content", results.get("msg1"));
    }

    @Test
    public void testProcessQueueFIFOOrder() {
        MessageProcessor processor = new MessageProcessor();
        processor.addHandler("INFO", content -> "Info: " + content);
        processor.addHandler("ERROR", content -> "Error: " + content);

        // Enqueue multiple messages
        processor.enqueueMessage("msg1", "INFO", "first");
        processor.enqueueMessage("msg2", "ERROR", "second");
        processor.enqueueMessage("msg3", "INFO", "third");

        Map<String, String> results = processor.processQueue();

        assertEquals(3, results.size());
        assertEquals("Info: first", results.get("msg1"));
        assertEquals("Error: second", results.get("msg2"));
        assertEquals("Info: third", results.get("msg3"));
    }

    @Test
    public void testProcessQueueWithDuplicateIds() {
        MessageProcessor processor = new MessageProcessor();
        processor.addHandler("INFO", content -> "Processed: " + content);

        // Enqueue messages with duplicate IDs
        processor.enqueueMessage("msg1", "INFO", "first");
        processor.enqueueMessage("msg1", "INFO", "duplicate");

        Map<String, String> results = processor.processQueue();

        // Since both messages have the same ID, the map will only contain one entry
        // The second message will overwrite the first in the results map
        assertEquals(1, results.size());
        assertEquals("Message already processed", results.get("msg1"));
    }

    @Test
    public void testProcessQueueWithNoHandler() {
        MessageProcessor processor = new MessageProcessor();

        processor.enqueueMessage("msg1", "UNKNOWN", "content");

        Map<String, String> results = processor.processQueue();

        assertEquals(1, results.size());
        assertEquals("No handler found for message type: UNKNOWN", results.get("msg1"));
    }

    @Test
    public void testProcessEmptyQueue() {
        MessageProcessor processor = new MessageProcessor();

        Map<String, String> results = processor.processQueue();

        assertTrue(results.isEmpty());
    }

    @Test
    public void testMessageFormatting() {
        MessageProcessor processor = new MessageProcessor();
        processor.addHandler("INFO", content -> content.toUpperCase());

        // Test that messages with pipe characters in content are handled correctly
        processor.enqueueMessage("msg1", "INFO", "content|with|pipes");

        Map<String, String> results = processor.processQueue();

        assertEquals(1, results.size());
        assertEquals("CONTENT|WITH|PIPES", results.get("msg1"));
    }
}

