import java.util.ArrayList;
import java.util.List;

class TaskManager {
    private List<String> tasks;
    private String filename;

    public TaskManager(String filename) {
        this.filename = filename;
        this.tasks = new ArrayList<>();
    }

    public boolean addTask(String taskDescription) {
        if (taskDescription == null || taskDescription.isEmpty()) {
            return false;
        }

        int taskNumber = tasks.size() + 1;
        String formattedTask = "[" + taskNumber + "]. " + taskDescription;
        tasks.add(formattedTask);
        return true;
    }

    public boolean deleteTask(String position) {
        if (position == null) {
            return false;
        }

        if ("-1".equals(position)) {
            tasks.clear();
            return true;
        }

        try {
            int index = Integer.parseInt(position);
            if (index < 1 || index > tasks.size()) {
                return false;
            }

            tasks.remove(index - 1); // Convert 1-based to 0-based index

            // Re-number remaining tasks
            for (int i = 0; i < tasks.size(); i++) {
                String taskContent = tasks.get(i);
                // Extract the description part after the number
                int dotIndex = taskContent.indexOf(". ");
                if (dotIndex != -1) {
                    String description = taskContent.substring(dotIndex + 2);
                    tasks.set(i, "[" + (i + 1) + "]. " + description);
                }
            }

            return true;
        } catch (NumberFormatException e) {
            return false;
        }
    }
}

