import java.util.*;

class PermissionInfo {
    private String uri;
    private String method;

    public PermissionInfo(String uri, String method) {
        this.uri = uri;
        this.method = method;
    }

    public String getUri() {
        return uri;
    }

    public String getMethod() {
        return method;
    }

    @Override
    public String toString() {
        return "PermissionInfo{uri='" + uri + "', method='" + method + "'}";
    }
}

class PermissionValidator {
    public boolean hasPermission(List<PermissionInfo> permissions, String requestUri, String requestMethod) {
        if (permissions == null || permissions.isEmpty()) {
            return false;
        }

        for (PermissionInfo permission : permissions) {
            if (permission != null && 
                matchesUri(permission.getUri(), requestUri) && 
                permission.getMethod().equalsIgnoreCase(requestMethod)) {
                return true;
            }
        }

        return false;
    }

    private boolean matchesUri(String permissionUri, String requestUri) {
        // Exact match first
        if (Objects.equals(permissionUri, requestUri)) {
            return true;
        }

        // Check for wildcard matching
        if (permissionUri.contains("{*}")) {
            return matchesWildcard(permissionUri, requestUri);
        }

        // Check for prefix matching (only if no wildcards)
        return matchesPrefix(permissionUri, requestUri);
    }

    private boolean matchesPrefix(String permissionUri, String requestUri) {
        // Request URI must be longer than permission URI
        if (requestUri.length() <= permissionUri.length()) {
            return false;
        }

        // Request URI must start with permission URI
        if (!requestUri.startsWith(permissionUri)) {
            return false;
        }

        // The character immediately after the permission URI must be '/'
        char nextChar = requestUri.charAt(permissionUri.length());
        return nextChar == '/';
    }

    private boolean matchesWildcard(String permissionUri, String requestUri) {
        String[] permissionSegments = permissionUri.split("/");
        String[] requestSegments = requestUri.split("/");

        // Must have same number of segments
        if (permissionSegments.length != requestSegments.length) {
            return false;
        }

        for (int i = 0; i < permissionSegments.length; i++) {
            String permissionSegment = permissionSegments[i];
            String requestSegment = requestSegments[i];

            if ("{*}".equals(permissionSegment)) {
                // Wildcard must match exactly one non-empty alphanumeric segment
                if (requestSegment.isEmpty() || !requestSegment.matches("[a-zA-Z0-9]+")) {
                    return false;
                }
            } else {
                // Non-wildcard segments must match exactly
                if (!Objects.equals(permissionSegment, requestSegment)) {
                    return false;
                }
            }
        }

        return true;
    }
}

