import java.util.*;

class BracketValidator {
    public static Map<String, Object> validateBrackets(String input) {
        Map<String, Object> result = new HashMap<>();
        Stack<Character> stack = new Stack<>();

        for (char c : input.toCharArray()) {
            // Only process bracket characters
            if (c == '(' || c == '[' || c == '{') {
                stack.push(c);
            } else if (c == ')' || c == ']' || c == '}') {
                if (stack.isEmpty()) {
                    result.put("balanced", false);
                    return result;
                }

                char top = stack.pop();
                if ((c == ')' && top != '(') ||
                    (c == ']' && top != '[') ||
                    (c == '}' && top != '{')) {
                    result.put("balanced", false);
                    return result;
                }
            }
            // Ignore all other characters
        }

        // Balanced if stack is empty (all brackets matched)
        result.put("balanced", stack.isEmpty());
        return result;
    }
}

