import java.util.*;

class BracketValidator {
    public static Map<String, Object> validateBrackets(String input) {
        Map<String, Object> result = new HashMap<>();

        // Handle empty string edge case
        if (input == null || input.isEmpty()) {
            result.put("balanced", true);
            result.put("errorPosition", -1);
            result.put("errorType", "");
            return result;
        }

        Stack<Integer> stack = new Stack<>();

        for (int i = 0; i < input.length(); i++) {
            char c = input.charAt(i);

            // Only process bracket characters
            if (c == '(' || c == '[' || c == '{') {
                stack.push(i);
            } else if (c == ')' || c == ']' || c == '}') {
                // Handle closing bracket when no opening bracket exists
                if (stack.isEmpty()) {
                    result.put("balanced", false);
                    result.put("errorPosition", i);
                    result.put("errorType", "mismatch");
                    return result;
                }

                int topIndex = stack.pop();
                char top = input.charAt(topIndex);
                if ((c == ')' && top != '(') ||
                    (c == ']' && top != '[') ||
                    (c == '}' && top != '{')) {
                    result.put("balanced", false);
                    result.put("errorPosition", i);
                    result.put("errorType", "mismatch");
                    return result;
                }
            }
            // Ignore all other characters
        }

        // Check if balanced - handle strings with no brackets
        if (stack.isEmpty()) {
            result.put("balanced", true);
            result.put("errorPosition", -1);
            result.put("errorType", "");
        } else {
            result.put("balanced", false);
            result.put("errorPosition", stack.get(0));
            result.put("errorType", "unclosed");
        }

        return result;
    }
}

