    private List<Integer> breakLabels;
    private List<Integer> continueLabels;

    public LoopAnalyzer() {
        this.loopCounter = 0;
        this.breakLabels = new ArrayList<>();
        this.continueLabels = new ArrayList<>();
    }

    public List<String> generateForLoop(String initVar, int initValue, 
                                      String conditionVar, int conditionValue,
                                      int stepValue) {
        List<String> intermediateCode = new ArrayList<>();
        loopCounter++;

        int headLabel = loopCounter * 1000 + 1;
        int tailLabel = loopCounter * 1000 + 2;
