import java.util.*;

class ScenarioAnalyzer {
    static class TreeNode {
        String name;
        List<TreeNode> children;
        boolean isSync;
        String serverName;

        TreeNode(String name) {
            this.name = name;
            this.children = new ArrayList<>();
            this.isSync = false;
            this.serverName = null;
        }

        TreeNode(String name, boolean isSync, String serverName) {
            this.name = name;
            this.children = new ArrayList<>();
            this.isSync = isSync;
            this.serverName = serverName;
        }

        void addChild(TreeNode child) {
            this.children.add(child);
        }
    }

    public Map<String, Object> analyzeScenario(TreeNode root) {
        Map<String, Object> result = new HashMap<>();
        Map<String, Integer> nodeIndices = new HashMap<>();
        Map<Integer, List<Integer>> syncPairs = new HashMap<>();

        if (root == null) {
            result.put("nodeIndices", nodeIndices);
            result.put("syncPairs", syncPairs);
            return result;
        }

        // Build node indices and collect nodes with parent info
        Queue<TreeNode> queue = new LinkedList<>();
        Map<TreeNode, TreeNode> parentMap = new HashMap<>();
        queue.offer(root);
        parentMap.put(root, null);
        int index = 0;

        while (!queue.isEmpty()) {
            TreeNode current = queue.poll();
            nodeIndices.put(current.name, index++);

            for (TreeNode child : current.children) {
                queue.offer(child);
                parentMap.put(child, current);
            }
        }

        // Find synchronous request-response pairs
        for (TreeNode node : parentMap.keySet()) {
            if (node.isSync) {
                TreeNode parent = parentMap.get(node);
                if (parent != null && parent.serverName != null) {
                    // Check children for matching serverName
                    for (TreeNode child : node.children) {
                        if (parent.serverName.equals(child.serverName)) {
                            int requestIndex = nodeIndices.get(node.name);
                            int responseIndex = nodeIndices.get(child.name);

                            // Initialize list if it doesn't exist
                            if (!syncPairs.containsKey(requestIndex)) {
                                syncPairs.put(requestIndex, new ArrayList<>());
                            }

                            syncPairs.get(requestIndex).add(responseIndex);
