import java.util.*;

class LinkedListNode {
    int val;
    LinkedListNode next;

    LinkedListNode(int val) {
        this.val = val;
        this.next = null;
    }
}

class LinkedListSwapper {
    public static LinkedListNode swapNodes(LinkedListNode head) {
        // Swap first two nodes (positions 0 and 1)
        LinkedListNode first = head;
        LinkedListNode second = head.next;

        // Adjust pointers
        first.next = second.next;
        second.next = first;

        // Return new head (previously second node)
        return second;
    }

    public static LinkedListNode createLinkedList(List<Integer> values) {
        if (values.isEmpty()) {
            return null;
        }

        LinkedListNode head = new LinkedListNode(values.get(0));
        LinkedListNode current = head;

        for (int i = 1; i < values.size(); i++) {
            current.next = new LinkedListNode(values.get(i));
            current = current.next;
        }

        return head;
    }

    public static List<Integer> linkedListToList(LinkedListNode head) {
        List<Integer> result = new ArrayList<>();
        LinkedListNode current = head;

        while (current != null) {
            result.add(current.val);
            current = current.next;
        }

        return result;
    }
}

