    public static LinkedListNode swapNodes(LinkedListNode head, int i, int j) {
        // Handle edge case where one of the indices is 0
        if (i == 0 || j == 0) {
            // Ensure i is the smaller index for consistency
            if (i > j) {
                int temp = i;
                i = j;
                j = temp;
            }

            // i is 0, j is some other position
            LinkedListNode prevJ = null;
            LinkedListNode nodeJ = head;

            // Find node at position j and its predecessor
            for (int k = 0; k < j; k++) {
                prevJ = nodeJ;
                nodeJ = nodeJ.next;
            }

            LinkedListNode nodeI = head; // Node at position 0

            // Perform the swap
            prevJ.next = nodeI;
            LinkedListNode temp = nodeI.next;
            nodeI.next = nodeJ.next;
            nodeJ.next = temp;

            return nodeJ; // New head
        }

        // Neither index is 0 - general case
        // Ensure i < j for consistency
        if (i > j) {
            int temp = i;
            i = j;
            j = temp;
