        // Find node at position j and its predecessor
        for (int k = 0; k < j; k++) {
            prevJ = nodeJ;
            nodeJ = nodeJ.next;
        }

        // Perform the swap
        prevI.next = nodeJ;
        prevJ.next = nodeI;

        LinkedListNode temp = nodeI.next;
        nodeI.next = nodeJ.next;
        nodeJ.next = temp;

