                nodeJ = nodeJ.next;
            }

            LinkedListNode nodeI = head; // Node at position 0

            // Perform the swap
            prevJ.next = nodeI;
            LinkedListNode temp = nodeI.next;
            nodeI.next = nodeJ.next;
            nodeJ.next = temp;

            return nodeJ; // New head
        }

        // Neither index is 0 - general case
        // Ensure i < j for consistency
        if (i > j) {
            int temp = i;
            i = j;
            j = temp;
        }

        LinkedListNode prevI = null, nodeI = head;
        LinkedListNode prevJ = null, nodeJ = head;

        // Find node at position i and its predecessor
        for (int k = 0; k < i; k++) {
            prevI = nodeI;
            nodeI = nodeI.next;
        }

        // Find node at position j and its predecessor
        for (int k = 0; k < j; k++) {
            prevJ = nodeJ;
            nodeJ = nodeJ.next;
        }

        // Perform the swap
        prevI.next = nodeJ;
        prevJ.next = nodeI;

        LinkedListNode temp = nodeI.next;
        nodeI.next = nodeJ.next;
        nodeJ.next = temp;

        return head;
    }

    public static LinkedListNode createLinkedList(List<Integer> values) {
        if (values.isEmpty()) {
            return null;
        }

        LinkedListNode head = new LinkedListNode(values.get(0));
        LinkedListNode current = head;

        for (int i = 1; i < values.size(); i++) {
            current.next = new LinkedListNode(values.get(i));
            current = current.next;
        }

        return head;
    }

    public static List<Integer> linkedListToList(LinkedListNode head) {
        List<Integer> result = new ArrayList<>();
        LinkedListNode current = head;

        while (current != null) {
            result.add(current.val);
            current = current.next;
        }

        return result;
    }
}

