import java.util.*;

class LinkedListNode {
    int val;
    LinkedListNode next;

    LinkedListNode(int val) {
        this.val = val;
        this.next = null;
    }
}

class LinkedListSwapper {
    public static LinkedListNode swapNodes(LinkedListNode head, int i, int j) {
        // Calculate the length of the linked list
        int length = 0;
        LinkedListNode current = head;
        while (current != null) {
            length++;
            current = current.next;
        }

        // Bounds checking: if either index is out of bounds (negative or >= length), return original head
        if (i < 0 || j < 0 || i >= length || j >= length) {
            return head;
        }

        // Handle the case where indices are equal - no swap needed
        if (i == j) {
            return head;
        }

        // Ensure i < j for consistency
        if (i > j) {
            int temp = i;
            i = j;
            j = temp;
        }

        // Find nodes and their predecessors
        LinkedListNode prevI = null, nodeI = head;
        LinkedListNode prevJ = null, nodeJ = head;

        // Find node at position i and its predecessor
        for (int k = 0; k < i; k++) {
            prevI = nodeI;
            nodeI = nodeI.next;
        }

        // Find node at position j and its predecessor
        for (int k = 0; k < j; k++) {
            prevJ = nodeJ;
            nodeJ = nodeJ.next;
        }

        // Handle adjacent nodes case (j = i + 1)
        if (j == i + 1) {
            // Adjacent nodes: nodeI -> nodeJ -> nodeJ.next
            // After swap: nodeJ -> nodeI -> nodeJ.next

            if (prevI != null) {
                prevI.next = nodeJ;
            }
            nodeI.next = nodeJ.next;
            nodeJ.next = nodeI;

            // Return new head if we swapped the head node
            return (i == 0) ? nodeJ : head;
        }

        // Non-adjacent nodes case
        // Update predecessor pointers
        if (prevI != null) {
            prevI.next = nodeJ;
        }
        if (prevJ != null) {
            prevJ.next = nodeI;
        }

        // Swap the next pointers
        LinkedListNode temp = nodeI.next;
        nodeI.next = nodeJ.next;
        nodeJ.next = temp;

        // Return new head if we swapped the head node (i == 0)
        return (i == 0) ? nodeJ : head;
    }

    public static LinkedListNode createLinkedList(List<Integer> values) {
        if (values.isEmpty()) {
            return null;
        }

        LinkedListNode head = new LinkedListNode(values.get(0));
        LinkedListNode current = head;

        for (int i = 1; i < values.size(); i++) {
            current.next = new LinkedListNode(values.get(i));
            current = current.next;
        }

        return head;
    }

    public static List<Integer> linkedListToList(LinkedListNode head) {
        List<Integer> result = new ArrayList<>();
        LinkedListNode current = head;

        while (current != null) {
            result.add(current.val);
            current = current.next;
        }

        return result;
    }
}

