import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.*;

class TestLinkedListSwapper {

    @Test
    public void testUnifiedLogicWithComplexHeadSwap() {
        // Test that unified logic correctly handles head swap with non-adjacent node
        // This should expose issues in previous round's separate head handling
        LinkedListNode head = LinkedListSwapper.createLinkedList(Arrays.asList(10, 20, 30, 40, 50, 60, 70));
        LinkedListNode result = LinkedListSwapper.swapNodes(head, 0, 5);
        List<Integer> resultList = LinkedListSwapper.linkedListToList(result);
        assertEquals(Arrays.asList(60, 20, 30, 40, 50, 10, 70), resultList);

        // Verify the new head is correct
        assertEquals(60, result.val);
    }

    @Test
    public void testUnifiedLogicWithReversedIndices() {
        // Test that unified logic handles reversed indices correctly in all cases
        // Previous round might have issues with index swapping in edge cases
        LinkedListNode head = LinkedListSwapper.createLinkedList(Arrays.asList(1, 2, 3, 4));
        LinkedListNode result = LinkedListSwapper.swapNodes(head, 3, 0); // Reversed: tail with head
        List<Integer> resultList = LinkedListSwapper.linkedListToList(result);
        assertEquals(Arrays.asList(4, 2, 3, 1), resultList);

        // Verify head changed correctly
        assertEquals(4, result.val);
    }

    @Test
    public void testUnifiedLogicWithSingleNodeList() {
        // Test edge case with single node - unified logic should handle gracefully
        LinkedListNode head = LinkedListSwapper.createLinkedList(Arrays.asList(42));
        LinkedListNode result = LinkedListSwapper.swapNodes(head, 0, 0);
        List<Integer> resultList = LinkedListSwapper.linkedListToList(result);
        assertEquals(Arrays.asList(42), resultList);

        // Verify head remains the same
        assertEquals(42, result.val);
    }

    @Test
    public void testUnifiedLogicWithThreeNodeComplexSwaps() {
        // Test three-node list with all possible swaps to ensure unified logic works
        LinkedListNode head = LinkedListSwapper.createLinkedList(Arrays.asList(100, 200, 300));

        // Swap head with tail (0, 2)
        LinkedListNode result1 = LinkedListSwapper.swapNodes(head, 0, 2);
        List<Integer> result1List = LinkedListSwapper.linkedListToList(result1);
        assertEquals(Arrays.asList(300, 200, 100), result1List);
        assertEquals(300, result1.val); // New head should be 300

        // Reset and swap adjacent nodes involving head (0, 1)
        head = LinkedListSwapper.createLinkedList(Arrays.asList(100, 200, 300));
        LinkedListNode result2 = LinkedListSwapper.swapNodes(head, 0, 1);
        List<Integer> result2List = LinkedListSwapper.linkedListToList(result2);
        assertEquals(Arrays.asList(200, 100, 300), result2List);
        assertEquals(200, result2.val); // New head should be 200
    }

    @Test
    public void testUnifiedLogicPointerIntegrity() {
        // Test that unified logic maintains proper pointer integrity in complex scenarios
        LinkedListNode head = LinkedListSwapper.createLinkedList(Arrays.asList(1, 2, 3, 4, 5, 6, 7, 8));

        // Perform multiple swaps that would stress the unified logic
        head = LinkedListSwapper.swapNodes(head, 0, 7); // Head with tail
        assertEquals(Arrays.asList(8, 2, 3, 4, 5, 6, 7, 1), LinkedListSwapper.linkedListToList(head));

        head = LinkedListSwapper.swapNodes(head, 1, 2); // Adjacent nodes
        assertEquals(Arrays.asList(8, 3, 2, 4, 5, 6, 7, 1), LinkedListSwapper.linkedListToList(head));

        head = LinkedListSwapper.swapNodes(head, 0, 3); // New head with middle
        assertEquals(Arrays.asList(4, 3, 2, 8, 5, 6, 7, 1), LinkedListSwapper.linkedListToList(head));

        // Verify final head
        assertEquals(4, head.val);
    }

    @Test
    public void testUnifiedLogicWithOutOfBoundsHandling() {
        // Test that unified logic properly handles out of bounds with various list sizes
        LinkedListNode head = LinkedListSwapper.createLinkedList(Arrays.asList(10, 20));

        // Test out of bounds scenarios
        LinkedListNode result1 = LinkedListSwapper.swapNodes(head, 0, 5);
        assertEquals(Arrays.asList(10, 20), LinkedListSwapper.linkedListToList(result1));
        assertEquals(10, result1.val); // Head should remain unchanged

        LinkedListNode result2 = LinkedListSwapper.swapNodes(head, -1, 1);
        assertEquals(Arrays.asList(10, 20), LinkedListSwapper.linkedListToList(result2));
        assertEquals(10, result2.val); // Head should remain unchanged
    }
}

