import java.util.*;
import java.net.URLDecoder;
import java.net.URLEncoder;
import java.io.UnsupportedEncodingException;
import java.util.regex.Pattern;
import java.util.regex.Matcher;

class WebServiceClient {
    public List<String> getPagedResults(String baseUrl, String endpoint, String whereClause, 
                                      int firstResult, int maxResults) throws RuntimeException {
        // Build the request URL with query parameters
        StringBuilder urlBuilder = new StringBuilder();
        urlBuilder.append(baseUrl).append(endpoint);

        // Add query parameters
        urlBuilder.append("?firstResult=").append(firstResult);
        urlBuilder.append("&maxResults=").append(maxResults);

        // URL-encode the whereClause if present
        if (whereClause != null && !whereClause.trim().isEmpty()) {
            try {
                String encodedWhereClause = URLEncoder.encode(whereClause, "UTF-8");
                urlBuilder.append("&whereClause=").append(encodedWhereClause);
            } catch (UnsupportedEncodingException e) {
                throw new RuntimeException("Failed to encode whereClause", e);
            }
        }

        String fullUrl = urlBuilder.toString();

        // Call simulateHttpRequest with the full URL
        String response = simulateHttpRequest(fullUrl);

        // Pass the response to parseResponseItems for extraction
        return parseResponseItems(response);
    }

    public List<String> parseResponseItems(String response) {
        List<String> items = new ArrayList<>();
        Pattern pattern = Pattern.compile("<ADColumn.*?</ADColumn>", Pattern.DOTALL);
