            for (String param : params) {
                String[] keyValue = param.split("=");
                if (keyValue.length == 2) {
                    try {
                        String key = URLDecoder.decode(keyValue[0], "UTF-8");
                        String value = URLDecoder.decode(keyValue[1], "UTF-8");

                        if ("firstResult".equals(key)) {
                            firstResult = Integer.parseInt(value);
                        } else if ("maxResults".equals(key) || "maxResult".equals(key)) {
                            maxResults = Integer.parseInt(value);
                        }
                    } catch (UnsupportedEncodingException | NumberFormatException e) {
                        // Ignore invalid parameters
                    }
                }
            }
        }

        // Limit maxResults to 50
        maxResults = Math.min(maxResults, 50);

        // Generate XML response
        StringBuilder xml = new StringBuilder();
        for (int i = 0; i < maxResults; i++) {
            int id = firstResult + i;
            xml.append("<ADColumn id=\"").append(id).append("\">").append("Column").append(id).append("</ADColumn>");
        }

        return xml.toString();
    }
}

