import org.junit.jupiter.api.Test;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.atomic.AtomicInteger;

class TestSystemShutdownManager {

    @Test
    public void testConcurrentShutdownPrevention() throws InterruptedException {
        Map<String, SystemShutdownManager.Service> services = new ConcurrentHashMap<>();
        CountDownLatch latch = new CountDownLatch(1);
        AtomicInteger shutdownCount = new AtomicInteger(0);

        // Service that blocks during shutdown to simulate concurrent access
        SystemShutdownManager.Service blockingService = () -> {
            try {
                latch.await(); // Block until released
                shutdownCount.incrementAndGet();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        };

        services.put("blocking", blockingService);
        SystemShutdownManager manager = new SystemShutdownManager(services, 5, TimeUnit.SECONDS);

        // Start first shutdown in separate thread
        Thread firstShutdown = new Thread(() -> {
            manager.shutdownAll();
        });
        firstShutdown.start();

        // Give first thread time to start and acquire the shutdown lock
        Thread.sleep(100);

        // Attempt second shutdown - should return false immediately
        boolean secondResult = manager.shutdownAll();
        assertFalse(secondResult, "Second shutdown should return false when one is already in progress");

        // Release the first shutdown
        latch.countDown();
        firstShutdown.join();

        // Verify only one shutdown actually executed
        assertEquals(1, shutdownCount.get(), "Only one shutdown should have executed");
    }

    @Test
    public void testShutdownAfterCompletion() {
        Map<String, SystemShutdownManager.Service> services = new ConcurrentHashMap<>();
        services.put("service1", () -> {});

        SystemShutdownManager manager = new SystemShutdownManager(services, 5, TimeUnit.SECONDS);

        // First shutdown should succeed
        assertTrue(manager.shutdownAll());

        // Second shutdown should also succeed (no longer in progress)
        assertTrue(manager.shutdownAll());
    }
}

