import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.atomic.AtomicBoolean;
import java.util.concurrent.atomic.AtomicInteger;

class TestThreadSynchronizer {

    @Test
    public void testLoopPreventsSpuriousWakeups() throws InterruptedException {
        ThreadSynchronizer synchronizer = new ThreadSynchronizer();
        AtomicBoolean resultReceived = new AtomicBoolean(true); // Start with true to detect false
        AtomicInteger wakeupCount = new AtomicInteger(0);
        CountDownLatch threadStarted = new CountDownLatch(1);

        Thread waitingThread = new Thread(() -> {
            threadStarted.countDown();
            boolean result = synchronizer.waitWithDeadline(3);
            resultReceived.set(result);
        });

        waitingThread.start();
        threadStarted.await();
        Thread.sleep(100); // Give thread time to enter wait

        // Simulate multiple spurious wakeups without setting the flag
        synchronizer.simulateSpuriousWakeup();
        Thread.sleep(50);
        synchronizer.simulateSpuriousWakeup();
        Thread.sleep(50);

        // Thread should still be waiting (not returned true from spurious wakeups)
        assertTrue(waitingThread.isAlive(), "Thread should still be waiting after spurious wakeups");

        // Now properly notify with flag set
        synchronizer.notifyAllThreads();
        waitingThread.join();

        assertTrue(resultReceived.get(), "Should return true only when flag is properly set");
    }

    @Test
    public void testFlagCheckedInLoop() throws InterruptedException {
        ThreadSynchronizer synchronizer = new ThreadSynchronizer();
        AtomicBoolean hasReturned = new AtomicBoolean(false);
        CountDownLatch threadStarted = new CountDownLatch(1);

        Thread waitingThread = new Thread(() -> {
            threadStarted.countDown();
            synchronizer.waitWithDeadline(5);
            hasReturned.set(true);
        });

        waitingThread.start();
        threadStarted.await();
        Thread.sleep(100);

        // Multiple spurious wakeups should not cause return
        for (int i = 0; i < 3; i++) {
            synchronizer.simulateSpuriousWakeup();
            Thread.sleep(50);
            assertFalse(hasReturned.get(), "Should not return from spurious wakeup " + (i + 1));
        }

        synchronizer.notifyAllThreads();
        waitingThread.join();
        assertTrue(hasReturned.get(), "Should return when properly signaled");
    }

    @Test
    public void testInterruptRestoresStatusAndReturnsFalse() throws InterruptedException {
        ThreadSynchronizer synchronizer = new ThreadSynchronizer();
        AtomicBoolean resultReceived = new AtomicBoolean(true); // Default true to detect false
        CountDownLatch threadStarted = new CountDownLatch(1);

        Thread waitingThread = new Thread(() -> {
            threadStarted.countDown();
            boolean result = synchronizer.waitWithDeadline(10);
            resultReceived.set(result);
            // Verify interrupt status is restored immediately after waitWithDeadline
            assertTrue(Thread.currentThread().isInterrupted(), "Interrupt status should be restored");
        });

        waitingThread.start();
        threadStarted.await();
        Thread.sleep(100);

        waitingThread.interrupt();
        waitingThread.join();

        assertFalse(resultReceived.get(), "Should return false when interrupted");
    }
}

