import java.util.concurrent.locks.Condition;
import java.util.concurrent.locks.ReentrantLock;
import java.util.Date;

class ThreadSynchronizer {
    private final ReentrantLock lock;
    private final Condition condition;
    private boolean isSignalled;

    public ThreadSynchronizer() {
        this.lock = new ReentrantLock();
        this.condition = lock.newCondition();
        this.isSignalled = false;
    }

    public boolean waitWithDeadline(int waitSeconds) {
        lock.lock();
        try {
            Date deadline = new Date(System.currentTimeMillis() + waitSeconds * 1000L);
            while (!isSignalled) {
                if (!condition.awaitUntil(deadline)) {
                    return false; // Timeout occurred
                }
            }
            return true; // Signal received
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            return false;
        } finally {
            isSignalled = false;
            lock.unlock();
        }
    }

    public void notifyAllThreads() {
        lock.lock();
        try {
            isSignalled = true;
            condition.signalAll();
        } finally {
            lock.unlock();
        }
    }

    // Expose condition for testing spurious wakeups
    public void simulateSpuriousWakeup() {
        lock.lock();
        try {
            condition.signalAll(); // Signal without setting flag
        } finally {
            lock.unlock();
        }
    }
}

