import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.Date;
import java.util.concurrent.CountDownLatch;
import java.util.concurrent.TimeUnit;

class TestThreadSynchronizer {

    @Test
    public void testAwaitUntilWithAbsoluteDeadline() throws InterruptedException {
        ThreadSynchronizer synchronizer = new ThreadSynchronizer();

        long startTime = System.currentTimeMillis();
        Date expectedDeadline = new Date(startTime + 1000); // 1 second from now

        // This should timeout after approximately 1 second
        boolean result = synchronizer.waitWithDeadline(1);
        long endTime = System.currentTimeMillis();
        long actualWaitTime = endTime - startTime;

        assertFalse(result, "Should timeout when no signal is received");
        // Allow some tolerance for timing precision (100ms)
        assertTrue(actualWaitTime >= 900 && actualWaitTime <= 1100, 
                  "Wait time should be close to 1 second, but was: " + actualWaitTime + "ms");
    }

    @Test
    public void testIsSignalledResetInFinally() throws InterruptedException {
        ThreadSynchronizer synchronizer = new ThreadSynchronizer();

        // Start a thread that will signal after a short delay
        Thread signalThread = new Thread(() -> {
            try {
                Thread.sleep(100);
                synchronizer.notifyAllThreads();
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        });

        signalThread.start();

        // First wait should succeed and receive the signal
        boolean firstResult = synchronizer.waitWithDeadline(2);
        assertTrue(firstResult, "First wait should succeed");

        signalThread.join();

        // Second wait should timeout because isSignalled should be reset to false
        long startTime = System.currentTimeMillis();
        boolean secondResult = synchronizer.waitWithDeadline(1);
        long waitTime = System.currentTimeMillis() - startTime;

        assertFalse(secondResult, "Second wait should timeout because isSignalled was reset");
        assertTrue(waitTime >= 900, "Should have waited for the full timeout period");
    }

    @Test
    public void testLockAlwaysReleasedInTryFinally() throws InterruptedException {
        ThreadSynchronizer synchronizer = new ThreadSynchronizer();
        CountDownLatch latch = new CountDownLatch(1);

        // Start a thread that will interrupt the waiting thread
        Thread interruptingThread = new Thread(() -> {
            try {
                Thread.sleep(100);
                Thread.currentThread().interrupt(); // This will cause InterruptedException
            } catch (InterruptedException e) {
                // Expected
            }
        });

        Thread waitingThread = new Thread(() -> {
            try {
                synchronizer.waitWithDeadline(5);
            } finally {
                latch.countDown(); // Signal that the method completed
            }
        });

        waitingThread.start();
        Thread.sleep(50);
        waitingThread.interrupt(); // Interrupt the waiting thread

        // Verify that the method completes (lock was released)
        assertTrue(latch.await(2, TimeUnit.SECONDS), "waitWithDeadline should complete even when interrupted");

        // Verify that another thread can acquire the lock (proving it was released)
        Thread testLockThread = new Thread(() -> {
            synchronizer.notifyAllThreads(); // This should not block
            latch.countDown();
        });

        CountDownLatch lockTestLatch = new CountDownLatch(1);
        Thread lockTest = new Thread(() -> {
            synchronizer.notifyAllThreads();
            lockTestLatch.countDown();
        });

        lockTest.start();
        assertTrue(lockTestLatch.await(1, TimeUnit.SECONDS), "Lock should be available for other threads");
    }
}

