import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.CompletableFuture;

class TestEnhancedWebSocketClient {

    @Test
    void testCloseAsyncWithNoConnectionDoesNotCallClose() {
        EnhancedWebSocketClient client = new EnhancedWebSocketClient("ws://example.com");

        // Test that closeAsync returns completed future when no connection exists
        CompletableFuture<Void> result = client.closeAsync();

        assertTrue(result.isDone(), "closeAsync should return already completed future when no connection exists");
        assertFalse(result.isCompletedExceptionally(), "closeAsync should complete normally when no connection exists");
        // The key test: no connection means no close() method should be called
        assertNull(client.connection, "No connection should exist");
    }

    @Test
    void testCloseAsyncWithInactiveConnectionDoesNotCallClose() throws Exception {
        EnhancedWebSocketClient client = new EnhancedWebSocketClient("ws://example.com");

        // First connect to create a connection
        client.connectAsync().get();

        // Close the connection to make it inactive
        client.connection.close();
        boolean wasClosedBefore = client.connection.wasCloseCalled();

        // Test that closeAsync returns completed future when connection is inactive
        CompletableFuture<Void> result = client.closeAsync();

        assertTrue(result.isDone(), "closeAsync should return already completed future when connection is inactive");
        assertFalse(result.isCompletedExceptionally(), "closeAsync should complete normally when connection is inactive");
        // The key test: inactive connection means close() should NOT be called again
        // Since close() was already called once, wasCloseCalled() will still be true
        // But we verify that closeAsync didn't attempt to close it again by checking the connection state
        assertFalse(client.connection.isConnected(), "Connection should remain closed");
        assertTrue(wasClosedBefore, "Connection was already closed before closeAsync");
    }

    @Test
    void testCloseAsyncWithActiveConnectionCallsClose() throws Exception {
        EnhancedWebSocketClient client = new EnhancedWebSocketClient("ws://example.com");

        // Connect to create an active connection
        client.connectAsync().get();

        // Verify connection is active before closing
        assertTrue(client.connection.isConnected(), "Connection should be active before closing");
        assertFalse(client.connection.wasCloseCalled(), "close() should not have been called yet");

        // Test that closeAsync calls close() on active connection
        CompletableFuture<Void> result = client.closeAsync();

        assertTrue(result.isDone(), "closeAsync should return completed future");
        assertFalse(result.isCompletedExceptionally(), "closeAsync should complete normally");
        assertFalse(client.connection.isConnected(), "Connection should be closed after closeAsync");
        // The key test: active connection means close() SHOULD be called
        assertTrue(client.connection.wasCloseCalled(), "close() should be called on active connection");
    }
}

