import java.util.HashMap;
import java.util.Map;

class TLSExtensionProcessor {

    public Map<String, Object> processExtension(String extensionType, 
                                              Map<String, Object> extensionData,
                                              Map<String, Object> context) {
        Map<String, Object> result = new HashMap<>();

        // Check if extensionType is null or empty
        if (extensionType == null || extensionType.isEmpty()) {
            throw new IllegalArgumentException("Extension type cannot be null or empty");
        }

        // Check if extensionData is null
        if (extensionData == null) {
            result.put("extensionType", extensionType);
            result.put("originalData", null);
            result.put("status", "error");
            result.put("message", "Extension data cannot be null");
            return result;
        }

        // Handle EARLY_DATA extension type
        if ("EARLY_DATA".equals(extensionType)) {
            result.put("extensionType", extensionType);
            result.put("originalData", extensionData);

            // Validate maxEarlyDataSize if present
            if (extensionData.containsKey("maxEarlyDataSize")) {
                Object maxEarlyDataSize = extensionData.get("maxEarlyDataSize");
                if (!(maxEarlyDataSize instanceof Integer) || (Integer) maxEarlyDataSize < 0) {
                    result.put("status", "error");
                    result.put("message", "maxEarlyDataSize must be a non-negative integer");
                    return result;
                }
                result.put("maxEarlyDataSize", maxEarlyDataSize);
            }

            // Validate connectionEnd (default to CLIENT if not present)
            String connectionEnd = "CLIENT";
            if (extensionData.containsKey("connectionEnd")) {
                Object connectionEndObj = extensionData.get("connectionEnd");
                if (connectionEndObj instanceof String) {
                    connectionEnd = (String) connectionEndObj;
                }
            }

            if (!"CLIENT".equals(connectionEnd) && !"SERVER".equals(connectionEnd)) {
                result.put("status", "error");
                result.put("message", "connectionEnd must be either CLIENT or SERVER");
                return result;
            }

            result.put("connectionEnd", connectionEnd);
            result.put("status", "success");

            // Update TLS context for successful processing
            updateTlsContext(extensionType, extensionData, context, result);

            return result;
        }

        // Handle SERVER_NAME extension type
        if ("SERVER_NAME".equals(extensionType)) {
            result.put("extensionType", extensionType);
            result.put("originalData", extensionData);

            // Validate serverName is present and is a non-empty string
            if (!extensionData.containsKey("serverName")) {
                result.put("status", "error");
                result.put("message", "Server name cannot be empty");
                return result;
            }

            Object serverNameObj = extensionData.get("serverName");
            if (!(serverNameObj instanceof String) || ((String) serverNameObj).isEmpty()) {
                result.put("status", "error");
                result.put("message", "Server name cannot be empty");
                return result;
            }

            result.put("serverName", serverNameObj);
            result.put("status", "success");

            // Update TLS context for successful processing
            updateTlsContext(extensionType, extensionData, context, result);

            return result;
        }

        // For all other extension types, they are unsupported
        result.put("extensionType", extensionType);
        result.put("originalData", extensionData);
        result.put("status", "warning");
        result.put("message", "Unsupported extension type");

        return result;
    }

    private void updateTlsContext(String extensionType, Map<String, Object> extensionData, 
                                 Map<String, Object> context, Map<String, Object> result) {
        // Store the entire extension data under the extensionType key in the context
        context.put(extensionType, extensionData);

        // Special handling for EARLY_DATA extension
        if ("EARLY_DATA".equals(extensionType)) {
            // Store maxEarlyDataSize directly in context if present
            if (extensionData.containsKey("maxEarlyDataSize")) {
                context.put("maxEarlyDataSize", extensionData.get("maxEarlyDataSize"));
            }

            // If connectionEnd is SERVER, store negotiatedExtensions with value EARLY_DATA
            String connectionEnd = "CLIENT"; // default
            if (extensionData.containsKey("connectionEnd")) {
                Object connectionEndObj = extensionData.get("connectionEnd");
                if (connectionEndObj instanceof String) {
                    connectionEnd = (String) connectionEndObj;
                }
            }

            if ("SERVER".equals(connectionEnd)) {
                context.put("negotiatedExtensions", "EARLY_DATA");
            }
        }

        // Include the updated context in the result
        result.put("updatedContext", new HashMap<>(context));
    }
}

