    void testHelperMethodsArePrivateAndUpdateTlsContextOnlyCalledWhenNotError() {
        TLSExtensionProcessor processor = new TLSExtensionProcessor();
        Map<String, Object> context = new HashMap<>();

        // Test that updateTlsContext is NOT called when status is error (null extension data)
        Map<String, Object> result1 = processor.processExtension("EARLY_DATA", null, context);
        assertEquals("error", result1.get("status"));
        assertEquals("Extension data cannot be null", result1.get("message"));
        assertNull(result1.get("updatedContext")); // Should not be present since updateTlsContext wasn't called
        assertTrue(context.isEmpty()); // Context should remain unchanged

        // Test that updateTlsContext IS called when status is success
        Map<String, Object> validData = new HashMap<>();
        validData.put("maxEarlyDataSize", 1024);
        Map<String, Object> result2 = processor.processExtension("EARLY_DATA", validData, context);
        assertEquals("success", result2.get("status"));
        assertNotNull(result2.get("updatedContext")); // Should be present since updateTlsContext was called
        assertFalse(context.isEmpty()); // Context should be updated

        // Test that updateTlsContext IS called when status is warning (unsupported extension)
        context.clear();
        Map<String, Object> result3 = processor.processExtension("UNKNOWN_EXT", validData, context);
        assertEquals("warning", result3.get("status"));
        assertEquals("Unsupported extension type", result3.get("message"));
        assertNotNull(result3.get("updatedContext")); // Should be present since updateTlsContext was called
