import java.util.HashMap;
import java.util.Map;
import java.util.ArrayList;
import java.util.List;

class GraphDatabaseManager {
    private Map<Long, Node> nodes;
    private Map<Long, Relationship> relationships;
    private long nextNodeId = 1;
    private long nextRelationshipId = 1;

    public GraphDatabaseManager() {
        nodes = new HashMap<>();
        relationships = new HashMap<>();
    }

    public long createNode(Map<String, Object> properties) {
        long nodeId = nextNodeId++;
        Node node = new Node(nodeId, properties);
        nodes.put(nodeId, node);
        return nodeId;
    }

    public long createRelationship(long startNodeId, long endNodeId, String type, Map<String, Object> properties) {
        if (!nodes.containsKey(startNodeId) || !nodes.containsKey(endNodeId)) {
            throw new IllegalArgumentException("Node not found: one or both specified nodes do not exist.");
        }

        long relationshipId = nextRelationshipId++;
        Relationship relationship = new Relationship(relationshipId, startNodeId, endNodeId, type, properties);
        relationships.put(relationshipId, relationship);
        return relationshipId;
    }

    public String getNodeInfo(long nodeId) {
        if (!nodes.containsKey(nodeId)) {
            throw new IllegalArgumentException("Error: Invalid node information requested.");
        }

        Node node = nodes.get(nodeId);
        StringBuilder result = new StringBuilder();

        // Node ID and properties
        result.append("Node ID: ").append(nodeId).append("\n");
        result.append("Properties: ").append(formatProperties(node.getProperties())).append("\n");

        // Find all relationships where this node is start or end
        List<String> relationshipStrings = new ArrayList<>();
        for (Relationship rel : relationships.values()) {
            if (rel.startNodeId == nodeId || rel.endNodeId == nodeId) {
                String relStr = String.format("[%d] %s (%d) -> (%d) %s", 
                    rel.id, rel.type, rel.startNodeId, rel.endNodeId, formatProperties(rel.properties));
                relationshipStrings.add(relStr);
            }
        }

        result.append("Relationships: ");
        if (relationshipStrings.isEmpty()) {
            result.append("None");
        } else {
            result.append(String.join(", ", relationshipStrings));
        }

        return result.toString();
    }

    private String formatProperties(Map<String, Object> properties) {
        if (properties.isEmpty()) {
            return "{}";
        }

        StringBuilder sb = new StringBuilder();
        sb.append("{");
        boolean first = true;
        for (Map.Entry<String, Object> entry : properties.entrySet()) {
            if (!first) {
                sb.append(", ");
            }
            sb.append(entry.getKey()).append("=").append(entry.getValue());
            first = false;
        }
        sb.append("}");
        return sb.toString();
    }

    private static class Node {
        private long id;
        private Map<String, Object> properties;

        public Node(long id, Map<String, Object> properties) {
            this.id = id;
            this.properties = new HashMap<>(properties);
        }

        public Map<String, Object> getProperties() {
            return properties;
        }
    }

    private static class Relationship {
        private long id;
        private long startNodeId;
        private long endNodeId;
        private String type;
        private Map<String, Object> properties;

        public Relationship(long id, long startNodeId, long endNodeId, String type, Map<String, Object> properties) {
            this.id = id;
            this.startNodeId = startNodeId;
            this.endNodeId = endNodeId;
            this.type = type;
            this.properties = new HashMap<>(properties);
        }
    }
}

