class TestGraphDatabaseManager {

    @Test
    public void testDeleteNodeRemovesNodeAndRelationships() {
        GraphDatabaseManager manager = new GraphDatabaseManager();

        // Create nodes
        long node1 = manager.createNode(new HashMap<>());
        long node2 = manager.createNode(new HashMap<>());
        long node3 = manager.createNode(new HashMap<>());

        // Create relationships
        manager.createRelationship(node1, node2, "KNOWS", new HashMap<>());
        manager.createRelationship(node2, node3, "LIKES", new HashMap<>());
        manager.createRelationship(node3, node1, "FOLLOWS", new HashMap<>());

        // Delete node2
        manager.deleteNode(node2);

        // Verify node2 is deleted by checking getNodeInfo throws exception
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class, () -> {
            manager.getNodeInfo(node2);
        });
        assertEquals("Error: Invalid node information requested.", exception.getMessage());

        // Verify node1 and node3 still exist but have fewer relationships
        String node1Info = manager.getNodeInfo(node1);
        String node3Info = manager.getNodeInfo(node3);

        // node1 should only have the relationship with node3 (node3 -> node1)
        assertTrue(node1Info.contains("FOLLOWS"));
        assertFalse(node1Info.contains("KNOWS"));

        // node3 should only have the relationship with node1 (node3 -> node1)
        assertTrue(node3Info.contains("FOLLOWS"));
        assertFalse(node3Info.contains("LIKES"));
    }

