        assertTrue(nodeInfo.contains("isolated"));

        // Delete the node
        manager.deleteNode(nodeId);

        // Verify node is deleted
        IllegalArgumentException exception = assertThrows(IllegalArgumentException.class, () -> {
            manager.getNodeInfo(nodeId);
        });
        assertEquals("Error: Invalid node information requested.", exception.getMessage());
    }

    @Test
    public void testDeleteNodeExceptionMessageFormat() {
        GraphDatabaseManager manager = new GraphDatabaseManager();

        // Test with different node IDs to verify message format
        IllegalArgumentException exception1 = assertThrows(IllegalArgumentException.class, () -> {
