import java.util.concurrent.*;

class ThreadExecutor {
    public static <T> T executeTaskWithTimeout(Callable<T> task, long timeoutMs) throws Exception {
        FutureTask<T> futureTask = new FutureTask<>(task);
        Thread workerThread = new Thread(futureTask);

        workerThread.start();

        try {
            return futureTask.get(timeoutMs, TimeUnit.MILLISECONDS);
        } catch (java.util.concurrent.TimeoutException e) {
            workerThread.interrupt();
            throw new TimeoutException("The task does not complete within the specified timeout period.");
        } catch (ExecutionException e) {
            throw new Exception("Task execution failed", e.getCause());
        } catch (InterruptedException e) {
            Thread.currentThread().interrupt();
            throw new Exception("Task was interrupted");
        }
    }
}

class SampleTask implements Callable<String> {
    private final String name;
    private final long sleepTime;

    public SampleTask(String name, long sleepTime) {
        this.name = name;
        this.sleepTime = sleepTime;
    }

    @Override
    public String call() throws Exception {
        System.out.println("Starting task: " + name);
        Thread.sleep(sleepTime);
        return "Result from " + name + " after " + sleepTime + "ms";
    }
}

