import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.*;

class TestThreadExecutor {

    @Test
    public void testInterruptedExceptionHandling() throws Exception {
        // Create a task that will run for a reasonable amount of time
        Callable<String> task = () -> {
            Thread.sleep(2000); // 2 seconds
            return "completed";
        };

        // Start the execution in a separate thread so we can interrupt it
        Thread testThread = new Thread(() -> {
            try {
                ThreadExecutor.executeTaskWithTimeout(task, 5000); // 5 second timeout
                fail("Expected InterruptedException to be handled");
            } catch (Exception e) {
                assertEquals("Task was interrupted", e.getMessage());
                assertTrue(Thread.currentThread().isInterrupted(), "Thread interrupt status should be restored");
            }
        });

        testThread.start();
        // Give the task a moment to start
        Thread.sleep(100);
        // Interrupt the test thread to trigger InterruptedException
        testThread.interrupt();
        testThread.join(1000); // Wait for completion
    }

    @Test
    public void testInterruptedExceptionMessage() {
        Callable<String> task = () -> {
            Thread.sleep(1000);
            return "completed";
        };

        // Interrupt current thread before calling the method
        Thread.currentThread().interrupt();

        Exception exception = assertThrows(Exception.class, () -> {
            ThreadExecutor.executeTaskWithTimeout(task, 2000);
        });

        assertEquals("Task was interrupted", exception.getMessage());
        assertTrue(Thread.currentThread().isInterrupted(), "Thread interrupt status should be restored");

        // Clear interrupt status for cleanup
        Thread.interrupted();
    }
}

