import java.util.*;

class AuthenticationService {
    public Map<String, Object> authenticateUser(String email, String password, String googleToken) {
        Map<String, Object> result = new HashMap<>();

        // Check if all credentials are null
        if (email == null && password == null && googleToken == null) {
            result.put("success", false);
            result.put("message", "Authentication failed: No credentials provided");
            return result;
        }

        // Check if email and password are non-null and non-empty
        if (email != null && !email.isEmpty() && password != null && !password.isEmpty()) {
            // Validate email format - must contain '@'
            if (!email.contains("@")) {
                result.put("success", false);
                result.put("message", "Invalid email or password");
                return result;
            }

            // Validate password length - must be at least 6 characters
            if (password.length() < 6) {
                result.put("success", false);
                result.put("message", "Invalid email or password");
                return result;
            }

            result.put("success", true);
            result.put("userEmail", email);
            result.put("authMethod", "email");
        } else if (email == null && password == null) {
            // Only attempt Google authentication if both email and password are null
            if (googleToken != null && googleToken.startsWith("google_")) {
                String username = googleToken.substring(7); // Remove 'google_' prefix
                result.put("success", true);
                result.put("userEmail", username + "@gmail.com");
                result.put("authMethod", "google");
            } else {
                result.put("success", false);
                result.put("message", "Google authentication failed");
            }
        } else {
            result.put("success", false);
            result.put("message", "Invalid email or password");
        }

        return result;
    }
}

