import java.util.*;

class TreePathAnalyzer {
    static class Node {
        int data;
        List<Node> children;

        public Node(int data) {
            this.data = data;
            this.children = new ArrayList<>();
        }

        public void addChild(Node child) {
            this.children.add(child);
        }
    }

    public List<Node> findLongestPath(Node root) {
        if (root == null) {
            return new ArrayList<>();
        }

        List<Node> longestPath = new ArrayList<>();
        dfs(root, new ArrayList<>(), longestPath);
        return longestPath;
    }

    private void dfs(Node node, List<Node> currentPath, List<Node> longestPath) {
        currentPath.add(node);

        // If this is a leaf node, check if current path is longer
        if (node.children.isEmpty()) {
            if (currentPath.size() > longestPath.size()) {
                longestPath.clear();
                longestPath.addAll(currentPath);
            }
        } else {
            // Continue DFS for all children
            for (Node child : node.children) {
                dfs(child, currentPath, longestPath);
            }
        }

        currentPath.remove(currentPath.size() - 1);
    }
}

