import java.util.*;

class TreePathAnalyzer {
    static class Node {
        int data;
        List<Node> children;

        public Node(int data) {
            this.data = data;
            this.children = new ArrayList<>();
        }

        public void addChild(Node child) {
            this.children.add(child);
        }

        @Override
        public String toString() {
            return String.valueOf(data);
        }
    }

    private List<Node> globalLongestPath = new ArrayList<>();
    private Set<Node> visited = new HashSet<>();

    public List<Node> findLongestPath(Node root) {
        if (root == null) {
            return new ArrayList<>();
        }

        globalLongestPath.clear();
        visited.clear();
        dfs(root);
        return new ArrayList<>(globalLongestPath);
    }

    // Expose visited set for testing O(N) guarantee
    public Set<Node> getVisitedNodes() {
        return new HashSet<>(visited);
    }

    // Expose global longest path for testing class-level variable updates
    public List<Node> getCurrentGlobalLongestPath() {
        return new ArrayList<>(globalLongestPath);
    }

    private List<Node> dfs(Node node) {
        // Guarantee O(N) by ensuring each node is visited exactly once
        if (visited.contains(node)) {
            return new ArrayList<>();
        }
        visited.add(node);

        if (node.children.isEmpty()) {
            List<Node> path = new ArrayList<>();
            path.add(node);

            // Update global longest path whenever a longer path is discovered
            if (path.size() > globalLongestPath.size()) {
                globalLongestPath = new ArrayList<>(path);
            }

            return path;
        }

        List<Node> firstLongest = new ArrayList<>();
        List<Node> secondLongest = new ArrayList<>();

        // Get the two longest paths from children
        for (Node child : node.children) {
            List<Node> childPath = dfs(child);

            if (childPath.size() > firstLongest.size()) {
                secondLongest = new ArrayList<>(firstLongest);
                firstLongest = new ArrayList<>(childPath);
            } else if (childPath.size() > secondLongest.size()) {
                secondLongest = new ArrayList<>(childPath);
            }
        }

        // Create path through this node combining two deepest child paths
        List<Node> pathThroughNode = new ArrayList<>();

        // Add first path in reverse (all nodes)
        for (int i = firstLongest.size() - 1; i >= 0; i--) {
            pathThroughNode.add(firstLongest.get(i));
        }

        // Add current node
        pathThroughNode.add(node);

        // Add second path (all nodes)
        if (!secondLongest.isEmpty()) {
            pathThroughNode.addAll(secondLongest);
        }

        // Update global longest path whenever a longer path is discovered
        if (pathThroughNode.size() > globalLongestPath.size()) {
            globalLongestPath = new ArrayList<>(pathThroughNode);
        }

        // Return the longest single path from this node (for parent's calculation)
        List<Node> singlePath = new ArrayList<>();
        singlePath.add(node);
        singlePath.addAll(firstLongest);

        return singlePath;
    }
}

