import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.*;

class TestTreePathAnalyzer {

    @Test
    public void testExactlyOneVisitPerNode() {
        TreePathAnalyzer analyzer = new TreePathAnalyzer();
        TreePathAnalyzer.Node root = new TreePathAnalyzer.Node(1);
        TreePathAnalyzer.Node child1 = new TreePathAnalyzer.Node(2);
        TreePathAnalyzer.Node child2 = new TreePathAnalyzer.Node(3);
        TreePathAnalyzer.Node grandchild1 = new TreePathAnalyzer.Node(4);
        TreePathAnalyzer.Node grandchild2 = new TreePathAnalyzer.Node(5);
        TreePathAnalyzer.Node grandchild3 = new TreePathAnalyzer.Node(6);

        root.addChild(child1);
        root.addChild(child2);
        child1.addChild(grandchild1);
        child1.addChild(grandchild2);
        child2.addChild(grandchild3);

        analyzer.findLongestPath(root);

        // Test O(N) guarantee: exactly N nodes should be visited once
        Set<TreePathAnalyzer.Node> visitedNodes = analyzer.getVisitedNodes();
        assertEquals(6, visitedNodes.size()); // All 6 nodes visited exactly once

        // Verify all nodes are in visited set
        assertTrue(visitedNodes.contains(root));
        assertTrue(visitedNodes.contains(child1));
        assertTrue(visitedNodes.contains(child2));
        assertTrue(visitedNodes.contains(grandchild1));
        assertTrue(visitedNodes.contains(grandchild2));
        assertTrue(visitedNodes.contains(grandchild3));
    }

    @Test
    public void testGlobalLongestPathMaintenance() {
        TreePathAnalyzer analyzer = new TreePathAnalyzer();
        TreePathAnalyzer.Node root = new TreePathAnalyzer.Node(1);
        TreePathAnalyzer.Node leftChild = new TreePathAnalyzer.Node(2);
        TreePathAnalyzer.Node rightChild = new TreePathAnalyzer.Node(3);

        root.addChild(leftChild);
        root.addChild(rightChild);

        // Create deeper left subtree
        TreePathAnalyzer.Node leftGrandchild = new TreePathAnalyzer.Node(4);
        TreePathAnalyzer.Node leftGreatGrandchild = new TreePathAnalyzer.Node(5);
        leftChild.addChild(leftGrandchild);
        leftGrandchild.addChild(leftGreatGrandchild);

        // Create shorter right subtree
        TreePathAnalyzer.Node rightGrandchild = new TreePathAnalyzer.Node(6);
        rightChild.addChild(rightGrandchild);

        List<TreePathAnalyzer.Node> result = analyzer.findLongestPath(root);

        // The class-level variable should maintain the longest path found
        List<TreePathAnalyzer.Node> globalPath = analyzer.getCurrentGlobalLongestPath();
        assertEquals(result.size(), globalPath.size());
        assertEquals(6, result.size()); // Path: 5-4-2-1-3-6

        // Verify the global path contains the expected nodes
        Set<Integer> globalPathData = new HashSet<>();
        for (TreePathAnalyzer.Node node : globalPath) {
            globalPathData.add(node.data);
        }
        assertEquals(Set.of(1, 2, 3, 4, 5, 6), globalPathData);
    }

    @Test
    public void testVisitedSetPreventsRevisiting() {
        TreePathAnalyzer analyzer = new TreePathAnalyzer();
        TreePathAnalyzer.Node root = new TreePathAnalyzer.Node(1);

        // Create a single-node tree
        analyzer.findLongestPath(root);

        Set<TreePathAnalyzer.Node> visitedAfterFirst = analyzer.getVisitedNodes();
        assertEquals(1, visitedAfterFirst.size());
        assertTrue(visitedAfterFirst.contains(root));

        // Call again - should clear visited set and reprocess
        analyzer.findLongestPath(root);

        Set<TreePathAnalyzer.Node> visitedAfterSecond = analyzer.getVisitedNodes();
        assertEquals(1, visitedAfterSecond.size());
        assertTrue(visitedAfterSecond.contains(root));
    }

    @Test
    public void testLargeTreeTimeComplexity() {
        TreePathAnalyzer analyzer = new TreePathAnalyzer();
        TreePathAnalyzer.Node root = new TreePathAnalyzer.Node(0);

        // Create a tree with many nodes to test O(N) behavior
        List<TreePathAnalyzer.Node> allNodes = new ArrayList<>();
        allNodes.add(root);

        // Create a balanced tree with 15 nodes
        for (int i = 1; i <= 14; i++) {
            TreePathAnalyzer.Node node = new TreePathAnalyzer.Node(i);
            allNodes.add(node);

            // Add to parent (create a somewhat balanced structure)
            TreePathAnalyzer.Node parent = allNodes.get((i - 1) / 2);
            parent.addChild(node);
        }

        analyzer.findLongestPath(root);

        // Verify exactly N nodes were visited (O(N) guarantee)
        Set<TreePathAnalyzer.Node> visited = analyzer.getVisitedNodes();
        assertEquals(15, visited.size());

        // Verify all nodes are in the visited set
        for (TreePathAnalyzer.Node node : allNodes) {
            assertTrue(visited.contains(node));
        }
    }
}

