        assertEquals(4.0, v.y, 1e-9);
    }

    @Test
    void testCelestialConstructor() {
        CelestialSimulator.Vector pos = new CelestialSimulator.Vector(1.0, 2.0);
        CelestialSimulator.Vector vel = new CelestialSimulator.Vector(3.0, 4.0);
        CelestialSimulator.Vector acc = new CelestialSimulator.Vector(5.0, 6.0);

        CelestialSimulator.Celestial body = new CelestialSimulator.Celestial("Earth", 100.0, pos, vel, acc);

        assertEquals("Earth", body.name);
        assertEquals(100.0, body.mass, 1e-9);
        assertEquals(1.0, body.position.x, 1e-9);
        assertEquals(2.0, body.position.y, 1e-9);
        assertEquals(3.0, body.velocity.x, 1e-9);
        assertEquals(4.0, body.velocity.y, 1e-9);
        assertEquals(5.0, body.acceleration.x, 1e-9);
        assertEquals(6.0, body.acceleration.y, 1e-9);
    }

    @Test
    void testSimulateBasicFunctionality() {
        CelestialSimulator.Vector pos = new CelestialSimulator.Vector(0.0, 0.0);
        CelestialSimulator.Vector vel = new CelestialSimulator.Vector(1.0, 0.0);
        CelestialSimulator.Vector acc = new CelestialSimulator.Vector(0.0, 1.0);

        CelestialSimulator.Celestial body = new CelestialSimulator.Celestial("TestBody", 1.0, pos, vel, acc);
        List<CelestialSimulator.Celestial> bodies = Arrays.asList(body);

        List<List<CelestialSimulator.Celestial>> history = CelestialSimulator.simulate(bodies, 1.0, 2);

        // Should have initial state + 2 simulation steps = 3 total states
        assertEquals(3, history.size());

        // Check initial state
        assertEquals(0.0, history.get(0).get(0).position.x, 1e-9);
        assertEquals(0.0, history.get(0).get(0).position.y, 1e-9);
        assertEquals(1.0, history.get(0).get(0).velocity.x, 1e-9);
        assertEquals(0.0, history.get(0).get(0).velocity.y, 1e-9);

        // Check first step: velocity updated, then position updated
        // v = v + a*dt = (1,0) + (0,1)*1 = (1,1)
        // p = p + v*dt = (0,0) + (1,1)*1 = (1,1)
        assertEquals(1.0, history.get(1).get(0).position.x, 1e-9);
        assertEquals(1.0, history.get(1).get(0).position.y, 1e-9);
        assertEquals(1.0, history.get(1).get(0).velocity.x, 1e-9);
        assertEquals(1.0, history.get(1).get(0).velocity.y, 1e-9);
    }

    @Test
    void testSimulateMultipleBodies() {
        CelestialSimulator.Celestial body1 = new CelestialSimulator.Celestial("Body1", 1.0, 
            new CelestialSimulator.Vector(0.0, 0.0), 
            new CelestialSimulator.Vector(1.0, 0.0), 
            new CelestialSimulator.Vector(0.0, 0.0));

        CelestialSimulator.Celestial body2 = new CelestialSimulator.Celestial("Body2", 2.0, 
            new CelestialSimulator.Vector(10.0, 10.0), 
            new CelestialSimulator.Vector(0.0, 1.0), 
            new CelestialSimulator.Vector(0.0, 0.0));

        List<CelestialSimulator.Celestial> bodies = Arrays.asList(body1, body2);

        List<List<CelestialSimulator.Celestial>> history = CelestialSimulator.simulate(bodies, 1.0, 1);

        assertEquals(2, history.size());
        assertEquals(2, history.get(0).size());
        assertEquals(2, history.get(1).size());

        // Verify both bodies are tracked correctly
        assertEquals("Body1", history.get(0).get(0).name);
        assertEquals("Body2", history.get(0).get(1).name);
    }
}

