import java.util.*;

class CelestialSimulator {
    static class Vector {
        double x, y;

        Vector(double x, double y) {
            this.x = x;
            this.y = y;
        }
    }

    static class Celestial {
        String name;
        double mass;
        Vector position;
        Vector velocity;
        Vector acceleration;

        Celestial(String name, double mass, Vector position, Vector velocity, Vector acceleration) {
            this.name = name;
            this.mass = mass;
            this.position = position;
            this.velocity = velocity;
            this.acceleration = acceleration;
        }

        void update(double timeStep) {
            // Update velocity: v = v0 + a*t
            velocity.x = velocity.x + acceleration.x * timeStep;
            velocity.y = velocity.y + acceleration.y * timeStep;

            // Update position: x = x0 + v*t
            position.x = position.x + velocity.x * timeStep;
            position.y = position.y + velocity.y * timeStep;
        }

        Vector calculateGravityForce(Celestial other) {
            final double G = 6.67430e-11;
            final double MIN_DISTANCE = 1e-10; // Minimum distance to avoid division by zero

            // Calculate distance vector
            double dx = other.position.x - this.position.x;
            double dy = other.position.y - this.position.y;

            // Calculate distance
            double distance = Math.sqrt(dx * dx + dy * dy);

            // Handle extremely small distances
            if (distance < MIN_DISTANCE) {
                distance = MIN_DISTANCE;
            }

            // Calculate gravitational force magnitude: F = G*m1*m2/r
            double forceMagnitude = G * this.mass * other.mass / (distance * distance);

            // Calculate unit vector components
            double unitX = dx / distance;
            double unitY = dy / distance;

            // Calculate force vector components
            double forceX = forceMagnitude * unitX;
            double forceY = forceMagnitude * unitY;

            return new Vector(forceX, forceY);
        }
    }

    private static List<Celestial> copyState(List<Celestial> bodies) {
        List<Celestial> copy = new ArrayList<>();
        for (Celestial body : bodies) {
            copy.add(new Celestial(body.name, body.mass, 
                new Vector(body.position.x, body.position.y),
                new Vector(body.velocity.x, body.velocity.y),
                new Vector(body.acceleration.x, body.acceleration.y)));
        }
        return copy;
    }

    static List<List<Celestial>> simulate(List<Celestial> bodies, double timeStep, int steps) {
        List<List<Celestial>> history = new ArrayList<>();

        // Create working copy of bodies
        List<Celestial> currentBodies = copyState(bodies);

        // Add initial state
        history.add(copyState(currentBodies));

        // Simulate for the specified number of steps
        for (int step = 0; step < steps; step++) {
            // Reset accelerations to zero
            for (Celestial body : currentBodies) {
                body.acceleration.x = 0;
                body.acceleration.y = 0;
            }

            // Calculate gravitational forces between all pairs
            for (int i = 0; i < currentBodies.size(); i++) {
                for (int j = 0; j < currentBodies.size(); j++) {
                    if (i != j) {
                        Celestial body1 = currentBodies.get(i);
                        Celestial body2 = currentBodies.get(j);

                        Vector force = body1.calculateGravityForce(body2);

                        // Update acceleration: a = F/m
                        body1.acceleration.x += force.x / body1.mass;
                        body1.acceleration.y += force.y / body1.mass;
                    }
                }
            }

            // Apply update method to each body
            for (Celestial body : currentBodies) {
                body.update(timeStep);
            }

            // Store deep copy of current state
            history.add(copyState(currentBodies));
        }

        return history;
    }
}

