import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.*;

class TestCelestialSimulator {

    @Test
    void testCreateSolarSystemReturnsCorrectNumberOfBodies() {
        List<CelestialSimulator.Celestial> solarSystem = CelestialSimulator.createSolarSystem();
        assertEquals(3, solarSystem.size(), "Solar system should contain exactly 3 bodies");
    }

    @Test
    void testCreateSolarSystemContainsCorrectBodies() {
        List<CelestialSimulator.Celestial> solarSystem = CelestialSimulator.createSolarSystem();

        Set<String> expectedNames = Set.of("Sun", "Earth", "Mars");
        Set<String> actualNames = new HashSet<>();

        for (CelestialSimulator.Celestial body : solarSystem) {
