        List<CelestialSimulator.Celestial> solarSystem = CelestialSimulator.createSolarSystem();

        CelestialSimulator.Celestial sun = null;
        for (CelestialSimulator.Celestial body : solarSystem) {
            if ("Sun".equals(body.name)) {
                sun = body;
                break;
            }
        }

        assertNotNull(sun, "Sun should be present in solar system");
        assertEquals(1.989e30, sun.mass, 1e28, "Sun should have realistic mass (~1.989e30 kg)");
        assertEquals(0, sun.position.x, 1e-10, "Sun should be at origin (x=0)");
        assertEquals(0, sun.position.y, 1e-10, "Sun should be at origin (y=0)");
    }

    @Test
