        assertEquals(5.972e24, earth.mass, 1e22, "Earth should have realistic mass (~5.972e24 kg)");
        assertEquals(1.496e11, earth.position.x, 1e9, "Earth should be at ~1 AU from Sun");
        assertEquals(0, earth.position.y, 1e-10, "Earth should start on x-axis");
        assertEquals(0, earth.velocity.x, 1e-10, "Earth should have zero x-velocity initially");
        assertEquals(29780, earth.velocity.y, 1000, "Earth should have realistic orbital velocity (~29,780 m/s)");
    }

    @Test
    void testMarsHasRealisticPropertiesForStableOrbit() {
        List<CelestialSimulator.Celestial> solarSystem = CelestialSimulator.createSolarSystem();

        CelestialSimulator.Celestial mars = null;
        for (CelestialSimulator.Celestial body : solarSystem) {
