import java.util.HashMap;
import java.util.Map;
import java.util.regex.Pattern;
import java.util.regex.PatternSyntaxException;

class RegexHelper {
    public Map<String, String> generateRegexPattern(String patternType, Map<String, String> parameters) {
        Map<String, String> result = new HashMap<>();
        String pattern = null;
        String description = null;

        if ("literal".equals(patternType)) {
            String text = parameters.get("text");
            pattern = escapeRegexChars(text);
            description = "Literal text";
        } else if ("characterClass".equals(patternType)) {
            String classType = parameters.get("class");

            if ("digit".equals(classType)) {
                pattern = "\\d";
                description = "Digit character class";
            } else if ("lowercase".equals(classType)) {
                pattern = "[a-z]";
                description = "Lowercase character class";
            } else if ("uppercase".equals(classType)) {
                pattern = "[A-Z]";
                description = "Uppercase character class";
            } else {
                pattern = ".";
                description = "Any character class";
            }
        } else if ("boundary".equals(patternType)) {
            String boundaryType = parameters.get("type");

            if ("start-line".equals(boundaryType)) {
                pattern = "^";
                description = "Start of line boundary";
            } else if ("end-line".equals(boundaryType)) {
                pattern = "$";
                description = "End of line boundary";
            } else if ("word".equals(boundaryType)) {
                pattern = "\\b";
                description = "Word boundary";
            } else if ("non-word".equals(boundaryType)) {
                pattern = "\\B";
                description = "Non-word boundary";
            } else {
                throw new IllegalArgumentException("Unknown boundary type: " + boundaryType);
            }
        } else if ("lookaround".equals(patternType)) {
            String type = parameters.get("type");
            String innerPattern = parameters.get("pattern");

            if ("ahead-positive".equals(type)) {
                pattern = "(?=" + innerPattern + ")";
                description = "Positive lookahead";
            } else if ("ahead-negative".equals(type)) {
                pattern = "(?!" + innerPattern + ")";
                description = "Negative lookahead";
            } else if ("behind-positive".equals(type)) {
                pattern = "(?<=" + innerPattern + ")";
                description = "Positive lookbehind";
            } else if ("behind-negative".equals(type)) {
                pattern = "(?<!" + innerPattern + ")";
                description = "Negative lookbehind";
            } else {
                throw new IllegalArgumentException("Unknown lookaround type: " + type);
            }
        } else if ("specialchar".equals(patternType)) {
            String type = parameters.get("type");

            if ("tab".equals(type)) {
                pattern = "\\t";
                description = "Tab character";
            } else if ("newline".equals(type)) {
                pattern = "\\n";
                description = "Newline character";
            } else if ("carriage-return".equals(type)) {
                pattern = "\\r";
                description = "Carriage return character";
            } else if ("space".equals(type)) {
                pattern = " ";
                description = "Space character";
            } else {
                throw new IllegalArgumentException("Unknown special character type: " + type);
            }
        }

        if (pattern != null) {
            // Validate the pattern
            try {
                Pattern.compile(pattern);
            } catch (PatternSyntaxException e) {
                throw e;
            }

            result.put("pattern", pattern);
            result.put("type", patternType);
            result.put("description", description);
        }

        return result;
    }

    private String escapeRegexChars(String text) {
        if (text == null) {
            return null;
        }
        return text.replaceAll("([\\\\\\[\\]\\(\\)\\{\\}\\*\\+\\?\\^\\$\\|\\.])", "\\\\$1");
    }
}

