                result.put("error", "Humedad incorrecta, debe estar entre 0 y 100.");
                return result;
            }
        } catch (NumberFormatException e) {
            result.put("isValid", false);
            result.put("error", "Humedad debe ser un nmero vlido.");
            return result;
        }

        // Validate fertilizer
        try {
