import java.util.concurrent.CompletableFuture;
import java.util.concurrent.TimeUnit;
import java.util.function.Supplier;

class AsyncOperationManager {

    public <T> CompletableFuture<T> executeWithTimeoutAndRetry(
            Supplier<T> taskSupplier,
            long timeout,
            TimeUnit timeUnit,
            T fallbackValue,
            int maxRetries) {
        return executeWithRetryInternal(taskSupplier, timeout, timeUnit, fallbackValue, maxRetries, 0);
    }

    private <T> CompletableFuture<T> executeWithRetryInternal(
            Supplier<T> taskSupplier,
            long timeout,
            TimeUnit timeUnit,
            T fallbackValue,
            int maxRetries,
            int currentAttempt) {

        CompletableFuture<T> future = CompletableFuture.supplyAsync(taskSupplier)
                .completeOnTimeout(fallbackValue, timeout, timeUnit);

        return future.handle((result, throwable) -> {
            // If successful and not a timeout fallback, return the result
            if (throwable == null && !result.equals(fallbackValue)) {
                return CompletableFuture.completedFuture(result);
            }

            // If we've exhausted retries, return the current result or exception
            if (currentAttempt >= maxRetries) {
                if (throwable != null) {
                    CompletableFuture<T> failedFuture = new CompletableFuture<>();
                    failedFuture.completeExceptionally(throwable);
                    return failedFuture;
                } else {
                    return CompletableFuture.completedFuture(result);
                }
            }

            // Retry with delay
            return CompletableFuture.supplyAsync(() -> {
                try {
                    Thread.sleep(500);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                    throw new RuntimeException(e);
                }
                return null;
            }).thenCompose(ignored -> 
                executeWithRetryInternal(taskSupplier, timeout, timeUnit, fallbackValue, maxRetries, currentAttempt + 1)
            );
        }).thenCompose(cf -> cf);
    }
}

