import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.atomic.AtomicInteger;
import java.util.function.Supplier;

class TestAsyncOperationManager {

    @Test
    void testRetryOnException() {
        AsyncOperationManager manager = new AsyncOperationManager();
        AtomicInteger attempts = new AtomicInteger(0);

        Supplier<String> taskSupplier = () -> {
            int attempt = attempts.incrementAndGet();
            if (attempt <= 2) {
                throw new RuntimeException("Task failed");
            }
            return "Success on attempt " + attempt;
        };

        CompletableFuture<String> result = manager.executeWithTimeoutAndRetry(
            taskSupplier, 1000, TimeUnit.MILLISECONDS, "fallback", 3
        );

        assertEquals("Success on attempt 3", result.join());
        assertEquals(3, attempts.get());
    }

    @Test
    void testRetryOnTimeout() {
        AsyncOperationManager manager = new AsyncOperationManager();
        AtomicInteger attempts = new AtomicInteger(0);

        Supplier<String> taskSupplier = () -> {
            int attempt = attempts.incrementAndGet();
            if (attempt <= 2) {
                try {
                    Thread.sleep(200); // Will timeout
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
                return "Should timeout";
            }
            return "Success on attempt " + attempt;
        };

        CompletableFuture<String> result = manager.executeWithTimeoutAndRetry(
            taskSupplier, 100, TimeUnit.MILLISECONDS, "fallback", 3
        );

        assertEquals("Success on attempt 3", result.join());
        assertTrue(attempts.get() >= 3);
    }

    @Test
    void testMaxRetriesExhausted() {
        AsyncOperationManager manager = new AsyncOperationManager();
        AtomicInteger attempts = new AtomicInteger(0);

        Supplier<String> taskSupplier = () -> {
            attempts.incrementAndGet();
            throw new RuntimeException("Task failure");
        };

        CompletableFuture<String> result = manager.executeWithTimeoutAndRetry(
            taskSupplier, 1000, TimeUnit.MILLISECONDS, "fallback", 2
        );

        assertThrows(RuntimeException.class, result::join);
        assertEquals(3, attempts.get()); // Initial attempt + 2 retries
    }

    @Test
    void testDelayBetweenRetries() {
        AsyncOperationManager manager = new AsyncOperationManager();
        AtomicInteger attempts = new AtomicInteger(0);
        long startTime = System.currentTimeMillis();

        Supplier<String> taskSupplier = () -> {
            int attempt = attempts.incrementAndGet();
            if (attempt <= 2) {
                throw new RuntimeException("Retry needed");
            }
            return "Success";
        };

        CompletableFuture<String> result = manager.executeWithTimeoutAndRetry(
            taskSupplier, 1000, TimeUnit.MILLISECONDS, "fallback", 3
        );

        assertEquals("Success", result.join());
        long duration = System.currentTimeMillis() - startTime;
        assertTrue(duration >= 1000, "Should have at least 1000ms delay (2 retries * 500ms)");
    }
}

