import java.util.concurrent.CompletableFuture;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.TimeoutException;
import java.util.function.Supplier;

class AsyncOperationManager {

    public <T> CompletableFuture<T> executeWithTimeoutAndRetry(
            Supplier<T> taskSupplier,
            long timeout,
            TimeUnit timeUnit,
            T fallbackValue,
            int maxRetries) {
        return executeWithRetryInternal(taskSupplier, timeout, timeUnit, fallbackValue, maxRetries, 0, null, false);
    }

    private <T> CompletableFuture<T> executeWithRetryInternal(
            Supplier<T> taskSupplier,
            long timeout,
            TimeUnit timeUnit,
            T fallbackValue,
            int maxRetries,
            int currentAttempt,
            Throwable lastException,
            boolean hasTimeoutOccurred) {

        CompletableFuture<T> future = CompletableFuture.supplyAsync(taskSupplier)
                .orTimeout(timeout, timeUnit);

        return future.handle((result, throwable) -> {
            // If successful, return the result
            if (throwable == null) {
                return CompletableFuture.completedFuture(result);
            }

            // Track if this failure was due to timeout
            boolean isTimeout = throwable instanceof TimeoutException ||
                               (throwable.getCause() instanceof TimeoutException);

            // Update tracking variables
            boolean newHasTimeoutOccurred = hasTimeoutOccurred || isTimeout;
            Throwable newLastException = isTimeout ? lastException : throwable;

            // If we've exhausted retries
            if (currentAttempt >= maxRetries) {
                if (newHasTimeoutOccurred && newLastException == null) {
                    // All failures were timeouts, return fallback
                    return CompletableFuture.completedFuture(fallbackValue);
                } else if (newLastException != null) {
                    // At least one exception occurred, complete exceptionally
                    CompletableFuture<T> failedFuture = new CompletableFuture<>();
                    failedFuture.completeExceptionally(newLastException);
                    return failedFuture;
                } else {
                    // Only timeouts occurred, return fallback
                    return CompletableFuture.completedFuture(fallbackValue);
                }
            }

            // Retry with delay
            return CompletableFuture.supplyAsync(() -> {
                try {
                    Thread.sleep(500);
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                    throw new RuntimeException(e);
                }
                return null;
            }).thenCompose(ignored -> 
                executeWithRetryInternal(taskSupplier, timeout, timeUnit, fallbackValue, maxRetries, currentAttempt + 1, newLastException, newHasTimeoutOccurred)
            );
        }).thenCompose(cf -> cf);
    }
}

