import static org.junit.jupiter.api.Assertions.*;
import org.junit.jupiter.api.Test;
import java.util.concurrent.CompletableFuture;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.ExecutionException;
import java.util.function.Supplier;

class TestAsyncOperationManager {

    @Test
    void testExceptionFirstThenTimeoutShouldCompleteExceptionally() {
        AsyncOperationManager manager = new AsyncOperationManager();

        RuntimeException testException = new RuntimeException("First attempt exception");
        int[] attemptCount = {0};

        Supplier<String> mixedTask = () -> {
            attemptCount[0]++;
            if (attemptCount[0] == 1) {
                // First attempt: throw exception
                throw testException;
            } else {
                // Subsequent attempts: timeout
                try {
                    Thread.sleep(200); // Longer than 100ms timeout
                } catch (InterruptedException e) {
                    Thread.currentThread().interrupt();
                }
                return "should timeout";
            }
        };

        CompletableFuture<String> result = manager.executeWithTimeoutAndRetry(
            mixedTask, 100, TimeUnit.MILLISECONDS, "fallback", 2
        );

        // Should complete exceptionally with the first exception, not return fallback
        ExecutionException exception = assertThrows(ExecutionException.class, () -> {
            result.get();
        });

        assertEquals(testException, exception.getCause());
    }

    @Test
    void testTimeoutOnlyFailuresShouldReturnFallback() throws Exception {
        AsyncOperationManager manager = new AsyncOperationManager();

        Supplier<String> timeoutTask = () -> {
            try {
                Thread.sleep(200); // Always timeout
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
            return "should not reach here";
        };

        CompletableFuture<String> result = manager.executeWithTimeoutAndRetry(
            timeoutTask, 100, TimeUnit.MILLISECONDS, "fallback", 2
        );

        // Should return fallback when all attempts are timeouts
        assertEquals("fallback", result.get());
    }

    @Test
    void testMultipleExceptionsKeepLastException() {
        AsyncOperationManager manager = new AsyncOperationManager();

        RuntimeException firstException = new RuntimeException("First exception");
        RuntimeException lastException = new RuntimeException("Last exception");
        int[] attemptCount = {0};

        Supplier<String> exceptionTask = () -> {
            attemptCount[0]++;
            if (attemptCount[0] == 1) {
                throw firstException;
            } else {
                throw lastException;
            }
        };

        CompletableFuture<String> result = manager.executeWithTimeoutAndRetry(
            exceptionTask, 1000, TimeUnit.MILLISECONDS, "fallback", 2
        );

        // Should complete exceptionally with the LAST exception
        ExecutionException exception = assertThrows(ExecutionException.class, () -> {
            result.get();
        });

        assertEquals(lastException, exception.getCause());
    }
}

