import java.util.LinkedList;
import java.util.Queue;
import java.util.concurrent.atomic.AtomicInteger;

class PollingQueue<T> {
    private final Queue<T> queue;
    private final AtomicInteger pollingCount;
    private final int maxPollingAttempts;

    public PollingQueue(int maxPollingAttempts) {
        this.queue = new LinkedList<>();
        this.pollingCount = new AtomicInteger(0);
        this.maxPollingAttempts = maxPollingAttempts;
    }

    public synchronized void enqueue(T element) {
        queue.add(element);
        notifyAll();
    }

    public synchronized T poll() {
        int currentAttempts = pollingCount.incrementAndGet();
        if (currentAttempts > maxPollingAttempts) {
            throw new IllegalStateException("Polling attempts exceeded maximum limit");
        }
        return queue.poll();
    }

    public synchronized T waitForElement(long timeoutMillis) {
        long startTime = System.currentTimeMillis();
        long endTime = startTime + timeoutMillis;

        while (queue.isEmpty() && System.currentTimeMillis() < endTime) {
            long remainingTime = endTime - System.currentTimeMillis();
            if (remainingTime <= 0) {
                break;
            }
            try {
                wait(remainingTime);
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
                return null;
            }
        }

        return queue.poll();
    }
}

