        assertTrue(endTime - startTime >= 100, "Should wait at least the timeout duration");
    }

    @Test
    void testWaitForElementWaitsForEnqueuedElement() throws InterruptedException {
        Thread enqueueThread = new Thread(() -> {
            try {
                Thread.sleep(50);
                queue.enqueue("delayed");
            } catch (InterruptedException e) {
                Thread.currentThread().interrupt();
            }
        });

        enqueueThread.start();
        String result = queue.waitForElement(200);
        enqueueThread.join();

        assertEquals("delayed", result);
    }

    @Test
    void testWaitForElementDoesNotAffectPollingCount() {
        queue.enqueue("test");
        queue.waitForElement(100);

        // Should still be able to poll normally without hitting the limit
        for (int i = 0; i < 5; i++) {
            queue.enqueue("item" + i);
            assertNotNull(queue.poll());
        }
    }
}

