import java.util.HashMap;
import java.util.Map;
import java.io.StringWriter;
import java.io.PrintWriter;
import java.util.logging.Logger;

class EnhancedExceptionHandler {
    private static final Logger logger = Logger.getLogger(EnhancedExceptionHandler.class.getName());

    public static Map<String, String> handleException(Exception exception, Map<String, String> context) {
        Map<String, String> errorReport = new HashMap<>();
        errorReport.put("exceptionType", exception.getClass().getName());
        errorReport.put("message", exception.getMessage());
        errorReport.put("timestamp", String.valueOf(System.currentTimeMillis()));

        // Capture full stack trace as string
        StringWriter stringWriter = new StringWriter();
        PrintWriter printWriter = new PrintWriter(stringWriter);
        exception.printStackTrace(printWriter);
        errorReport.put("stackTrace", stringWriter.toString());

        // Handle null context gracefully and merge context if not null
        if (context != null) {
            errorReport.putAll(context);
        }

        // Log detailed error message
        StringBuilder logMessage = new StringBuilder();
        logMessage.append("Exception handled - Type: ").append(exception.getClass().getName());
        logMessage.append(", Message: ").append(exception.getMessage());
        if (context != null && !context.isEmpty()) {
            logMessage.append(", Context: ").append(context.toString());
        }
        logger.severe(logMessage.toString());

        return errorReport;
    }

    public static void performBusinessOperation(String operationType) throws CustomBusinessException {
        if (operationType == null) {
            throw new IllegalArgumentException("Invalid operation type: null");
        }

        switch (operationType) {
            case "divideByZero":
                throw new ArithmeticException("Division by zero operation");
            case "nullPointer":
                throw new NullPointerException("Null pointer operation");
            case "arrayIndex":
                throw new ArrayIndexOutOfBoundsException("Array index out of bounds operation");
            case "custom":
                throw new CustomBusinessException("Custom business operation");
            default:
                throw new IllegalArgumentException("Invalid operation type: " + operationType);
        }
    }
}

class CustomBusinessException extends Exception {
    public CustomBusinessException(String message) {
        super(message);
    }
}

